import React, { useState } from 'react';
import {v4 as uuidV4} from 'uuid';
import toast from 'react-hot-toast';
import {useNavigate} from 'react-router-dom'; //Redirect

const Home = () => {
  
   const navigate = useNavigate();

  const [roomId, setRoomId] = useState('');
  const [username, setusername] = useState('');

  const createNewRoom = (e) => {
       e.preventDefault();
       const id = uuidV4();
       setRoomId(id);
       toast.success('created a new room');
  };

  const joinRoom = () => {
    if(!roomId || !username){
        toast.error('ROOM ID & username is required');
        return;
    } 

    // Redirect
    navigate(`/editor/${roomId}`,{
        state: {
            username,
        }
    })
  }

  return (
    <div className="homePageWrapper">
        <div className="formWrapper">
          <img clasName="homeLogo" src="/app_logo.png" alt='Home Logo'/>
          <h4 className="mainLabel">Paste invitation ROOM ID</h4>
          <div className="inputGroup">
             <input type="text" className="inputBox" placeholder='ROOM ID' 
       onChange={(e) => setRoomId(e.target.value)} value={roomId} onKeyUp={handleInputEnter}/>
             <input type="text" className="inputBox" placeholder='USERNAME' 
             onChange={(e) => setusername(e.target.value)} value={username} onKeyUp={handleInputEnter}/>
             <button className="btn joinBtn" onClick={joinRoom}>Join</button>
             <span className="createInfo">
                If you don't have invite then create &nbsp;<a href="" onClick={createNewRoom} className="createNewBtn">new room</a>
             </span>
          </div>
        </div>
        <footer>
            <h4>
               Build with ðŸ’› by &nbsp; <a href="https://github.com/deepak-812">Deepak Sharma</a>
            </h4>
        </footer>
    </div>
  )
}

export default Home
